sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }


   var whatNeeded = g_form.getValue('u_what_needed');

   g_form.clearOptions('u_what_needed');


   if(newValue == 'hr'){
	g_form.addOption("u_what_needed", "hr1", "Human Resources 1");
	g_form.addOption("u_what_needed", "hr2", "Human Resources 2");
	g_form.addOption("u_what_needed", "other", "Other");
   }
   if(newValue == 'legal'){
	g_form.addOption("u_what_needed", "legal1", "Legal1");
	g_form.addOption("u_what_needed", "legal2", "Legal2");
	g_form.addOption("u_what_needed", "other", "Other");
   }
   if(newValue == 'facilities'){
	g_form.addOption("u_what_needed", "facilities 1", "Facilities1");
	g_form.addOption("u_what_needed", 'facilities 2', "Facilities 2");
	g_form.addOption("u_what_needed", 'other', "Other");
   }

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue("u_what_needed", whatNeeded);
	}

   //Type appropriate comment here, and begin script below

}
